const host = window.location.hostname

let URL;

if (window.location.hostname.includes('ibict.br')) {
  URL = `https://${window.location.host}/${window.location.pathname.split('/')[1]}/api/v1`;
} else {
  URL = `http://${window.location.host}/${window.location.pathname.split('/')[1]}/api/v1`;
}



document.addEventListener('DOMContentLoaded', function () {

  function getIdFromURL() {
    const currentUrl = window.location.href;
    const idMatch = currentUrl.match(/Record\/(\d+-[a-fA-F0-9-]+)/);

    if (idMatch && idMatch[1]) {
      return idMatch[1];
    } else {
      console.log('ID não encontrado na URL');
      return null;
    }
  }

  document.getElementById('pdfButton').addEventListener('click', function () {
    const revistaId = getIdFromURL();
    gerarPDF(revistaId);
  });


});

async function gerarPDF(revistaId) {
  // const revistaId = getIdFromURL();

  if (!revistaId) {
    console.log('ID da revista não encontrado na URL.');
    return;
  }
  console.log("id", revistaId);
  try {
    let RecordURL = `record?id=${revistaId}&field[]=title&field[]=publisher&field[]=issns&field[]=urls&field[]=sealColor&field[]=lastModified&field[]=dc.contributor.editor&field[]=dc.rights.preprint&field[]=dc.rights.authorpostprint&field[]=dc.rights.journalpostprint&field[]=dc.rights.acess&field[]=dc.rights.creativecommons&field[]=dc.identifier.email&field[]=dc.description.phone`;

    let response = await fetch(`${URL}/${RecordURL}`);


    let data = await response.json();

    if (!response.ok) {
      console.error(`Erro na requisição: ${response.statusText}`);
      return;
    }


    console.log("O que está vindo da api", data)

    if (data && data.records.length > 0) {
      gerarPDfComDados(data.records[0]);
    } else {
      console.log('Nenhum dado retornado da API.');
    }
  } catch (error) {
    console.error('Ocorreu um erro ao buscar a revista:', error);
  }
}


function gerarPDfComDados(record) {
  if (window.jspdf && window.jspdf.jsPDF && html2canvas) {
    const { jsPDF } = window.jspdf;
    const doc = new jsPDF({
      orientation: 'portrait',
      unit: 'mm',
      format: 'a4'
    });

    const margin = 10;
    const pageWidth = doc.internal.pageSize.width - margin * 2;
    const pageHeight = doc.internal.pageSize.height - margin * 2;


    const titulo = record['title'] || 'Título não disponível';
    const publicacao = record['publisher'] || 'Editora não disponível';
    const issn = record['issns'] && record['issns'].length > 0 ? record['issns'][0] : 'ISSN não disponível';
    const urlObj = record['urls'] && record['urls'].length > 0 ? record['urls'][0] : null;
    const link = urlObj ? urlObj.url : 'URL indisponível';
    const selo = record['sealColor'] || 'Selo indisponível';
    const dataModificacoes = record['lastModified'] || 'Não disponível ';
    const editor = record['dc.contributor.editor'] || 'Editor não disponível';
    const preprint = record['dc.rights.preprint'] || 'Informação indisponível';
    const authorPostprint = record['dc.rights.authorpostprint'] || 'Informação indisponível';
    const journalPostprint = record['dc.rights.journalpostprint'] || 'Informação indisponível';
    const tipoAcesso = record['dc.rights.access'] || 'Informação indisponível';
    const licencaCC = record['dc.rights.creativecommons'] || 'Informação indisponível';
    const email = record['dc.identifier.email'] || 'Email indisponível';
    const telefone = record['dc.description.phone'] || 'Telefone indisponível';
    const imgInicio = 'data:image/jpeg;base64,';
    const imgFim = 'data:image/png;base64,';


    const contentElement1 = document.createElement('div');
    contentElement1.innerHTML = `
      <style>
        h1 {
          font-size: 16pt;
          font-weight: 700;
          padding-top:15px;
          padding-bottom:15px;
        }
        p {
          font-size: 14pt;
          line-height: 1.6;
          text-align: justify;
          font-family: 'Open Sans', sans-serif;
          color: #333333;
        }
        a {
          text-decoration: underline;
          color: #333333;
        }
      </style>
      <h1>Política Editorial do(a) ${titulo}</h1>
      <p>O (a) ${titulo} é uma publicação oficial do (a) ${publicacao}, registrada sob o número de ISSN ${issn}, e tem como editor (a) responsável ${editor}.</p>
      <p>Sua página oficial é: <a href="${link}" target="_blank">${link}</a></p>
      <p>O (a) ${titulo} aplica a seguinte política em relação ao depósito e acesso de seus artigos em repositórios digitais/institucionais:</p>
      <p><strong>Versão preprint:</strong> ${preprint}</p>
      <p><strong>Versão pós-print do autor:</strong> ${authorPostprint}</p>
      <p><strong>Versão pós-print da revista:</strong> ${journalPostprint}</p>  
      <p>Por conta destas permissões, foi atribuído o seguinte selo à (ao) ${titulo}: ${selo}</p>
      <p>O (a) ${titulo} permite o seguinte tipo de acesso aos documentos publicados: ${tipoAcesso}</p>
      <p>Estes documentos são licenciados sob a seguinte Licença Creative Commons: ${licencaCC}</p>
    `;

    contentElement1.style.width = `${pageWidth}mm`;
    contentElement1.style.padding = `${margin}mm`;
    contentElement1.style.boxSizing = 'border-box';

    document.body.appendChild(contentElement1);

    html2canvas(contentElement1).then((canvas1) => {

      const imgData = canvas1.toDataURL('image/png');
      const imgWidth = 210;
      const imgHeight = (canvas1.height * imgWidth) / canvas1.width;
      let yPosition = 40;

      doc.addImage(imgInicio, 'PNG', margin, margin, 60, 30);


      doc.addImage(imgData, 'PNG', margin, yPosition, imgWidth - 2 * margin, imgHeight);
      yPosition += imgHeight;

      doc.addPage();
      const contentElement2 = document.createElement('div');
      contentElement2.innerHTML = `
        <style>
        .container{
          width:90%;
          box-sizing: border-box;
          margin:0;
          padding: 10px;
          page-break-inside: avoid; /* Evita quebras de página no meio do contêiner */
        }
        .container p {
          font-size: 14pt;
          line-height: 1.6;
          text-align: justify;
          font-family: 'Open Sans', sans-serif;
          color: #333333;
        }

        hr {
          border: none;
          border-top: 1px solid #E1E0E0;
          margin-top: 10px;
          margin-bottom: 20px;
          width:100%;
          box-sizing: border-box;
          text-align:center;

          }

          .container p strong{
            display: block;
            margin-top:20px;

          }

          .centralizado {
            text-align: center;
            margin: 0 auto;

          }
          .container p i{
            padding-bottom: 25px;
            color: #6A6A6A;
          }
          p + p {
          margin-top:20px
          }
        </style>
        <div class=container>
        <p>Todos os dados aqui apresentados foram informados pela equipe editorial e validados pela equipe do Diretório de políticas editoriais das revistas científicas brasileiras (Diadorim).</p>
        <p><i>A última data e horário de atualização destes dados foi: ${dataModificacoes}</i></p>
        <hr class="centralizado" />
        <p><strong>Para dúvidas ou mais informações, entre em contato com a equipe editorial por meio dos seguintes canais:</strong></p>
        <p>E-mail: ${email}</p>
        <p>Telefone: ${telefone}</p>
        </div>
      `;


      contentElement2.style.width = `${pageWidth}mm`;
      contentElement2.style.padding = `${margin}mm`;
      contentElement2.style.boxSizing = 'border-box';
      contentElement2.style.margin = 'auto';

      document.body.appendChild(contentElement2);

      

      html2canvas(contentElement2).then((canvas2) => {
        const imgData2 = canvas2.toDataURL('image/png');
        const imgWidth = 210;
        const imgHeight2 = (canvas2.height * pageWidth) / canvas2.width;
        let yPosition = 60;
        

        doc.addImage(imgData2, 'PNG', margin, margin, imgWidth, imgHeight2);
        yPosition += imgHeight;
        const imgFimWidth = 50;
        const imgFimHeight = 30;
        const imgFimX = pageWidth - imgFimWidth - margin;
        const imgFimY = pageHeight - imgFimHeight - margin;
      
        doc.addImage(imgFim, 'PNG', imgFimX, imgFimY, imgFimWidth, imgFimHeight);

        const tituloFormatado = titulo.normalize('NFD').replace(/[\u0300-\u036f]/g, "");
        doc.save(`${tituloFormatado}.pdf`);

        document.body.removeChild(contentElement1);
        document.body.removeChild(contentElement2);
      });
    });
  } else {
    console.log("Erro ao carregar as bibliotecas necessárias.");
  }
}
